function init(){const t=document.getElementById("form"),e=document.getElementById("input"),s=document.getElementById("todo__list");t.addEventListener("submit",(t=>{t.preventDefault();const n=`<li class="todo__item task">\n    <button class="task__status"></button>\n    <p class="task__message">${e.value}</p>\n    <button class="task__delete-button">\n    <img class="task__delete-img" src="images/delete.svg" alt="Крест кнопки удаления">\n    </button>\n    </li>`;s.insertAdjacentHTML("beforeend",n),e.value=""}));[...document.getElementsByClassName("task__status")].forEach((t=>{t.addEventListener("click",(()=>{t.parentNode.classList.toggle("task_complete")}))}))}window.onload=init;