import*as utils from"./utils.js";import*as storage from"./localStorage.js";import*as task from"./task.js";function init(){const t=document.getElementById("form"),e=document.getElementById("input"),n=document.getElementById("todo__list"),o=inputHandler(e,n);storage.load(n),t.addEventListener("submit",o),e.addEventListener("blur",o),utils.createControls(n),window.innerWidth<401&&e.setAttribute("placeholder","Что выполнить?")}function inputHandler(t,e){return n=>{n.preventDefault(),""!==t.value.trim()&&(task.createTask(e,t.value),storage.save(e),utils.updateAmountOfActiveTasks(e)),t.value=""}}window.onload=init;